package nl.oose.blackpool.Services.adapters;

import nl.oose.blackpool.DTO.Face;
import nl.oose.blackpool.DTO.ImageDTO;
import nl.oose.blackpool.DTO.ListOfFacesDTO;
import nl.oose.blackpool.Exceptions.CouldNotLoadFileException;
import nl.oose.blackpool.Exceptions.IncorrectBase64Exception;
import org.junit.Before;
import org.junit.Rule;
import org.junit.Test;
import org.junit.rules.ExpectedException;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;
import org.opencv.objdetect.CascadeClassifier;


import static org.junit.Assert.assertEquals;
import static org.mockito.ArgumentMatchers.anyString;
import static org.mockito.Mockito.when;

@RunWith(MockitoJUnitRunner.class)
public class OpenCVAdapterTest {

    @InjectMocks
    private OpenCVAdapter sut;

    @Mock
    private CascadeClassifier faceCascade;


    private String base64;
    private ListOfFacesDTO listOfFaces;
    private static final String EMPTY_BASE64 = "";

    @Before
    public void setup () {
        base64 = "data:image/jpeg;base64,"+""+""+"";
        listOfFaces = new ListOfFacesDTO();
        Face f1 = new Face(1,1,5,5,true);
        Face f2 = new Face(2,2,10,10,true);
        listOfFaces.addFaceToListOfFaces(f1);
        listOfFaces.addFaceToListOfFaces(f2);
    }
    @Rule
    public final ExpectedException exception = ExpectedException.none();

    @Test
    public void executeRecognitionTestThrowsCouldNotLoadFileException() throws IncorrectBase64Exception, CouldNotLoadFileException {
        //Arrange
        when(faceCascade.load(anyString())).thenReturn(false);

        //Assert
        exception.expect(CouldNotLoadFileException.class);

        //Act
        sut.executeRecognition(base64);

    }


    @Test
    public void executeRecognitionTestEmptyStringThrowsIncorrectBase64Exception() throws IncorrectBase64Exception, CouldNotLoadFileException {
        // Assert
        exception.expect(IncorrectBase64Exception.class);
        when(faceCascade.load(anyString())).thenReturn(true);
        // Act
        sut.executeRecognition(EMPTY_BASE64);
    }

    @Test
    public void executeRecognitionTestReturnType () throws IncorrectBase64Exception, CouldNotLoadFileException {
        //Arrange
        ListOfFacesDTO expected = new ListOfFacesDTO();
        when(faceCascade.load(anyString())).thenReturn(true);

        //Act
        ListOfFacesDTO actual = sut.executeRecognition(base64);

        //Assert
        assertEquals(expected.getClass(),actual.getClass());
    }

    @Test
    public void executeBlurTestReturnType() throws IncorrectBase64Exception, CouldNotLoadFileException {
        //Arrange
        ImageDTO expected = new ImageDTO();
        when(faceCascade.load(anyString())).thenReturn(true);

        //Act
        ImageDTO actual = sut.executeBlur(base64, listOfFaces);

        //Assert
        assertEquals(expected.getClass(),actual.getClass());
    }

    @Test
    public void executeBlurTestEmptyStringThrowsIncorrectBase64Exception() throws IncorrectBase64Exception, CouldNotLoadFileException {
        // Arrange
        when(faceCascade.load(anyString())).thenReturn(true);
        // Assert
        exception.expect(IncorrectBase64Exception.class);
        // Act
        sut.executeBlur(EMPTY_BASE64, listOfFaces);
    }



}
